package com.mycompany.ch04.tr;

public class If3tr {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//int 타입의 변수를 1개 선언하고
		//짝수인지 홀수인지 판단하여 결과를 출력하는 프로그램을 적성하시오
		
		int a = 4;
		
		if (a % 2 == 0) {
			System.out.println("짝수입니다.");
		} else {
			System.out.println("홀수입니다.");
		}
		
	}

}
